

$(document).ready(function () {
    "use strict";

    $(".panel-body").on('click', 'a[href^="#"]', function (ev) {
        var targetId = $(ev.target).attr('href'),
            $target = $(targetId);
        $target.parents('.collapse').addClass('in').css({ height: '' });
    });

    /* Collapse/Expand all buttons */

    //$('.expand-glossary, .collapse-glossary').attr('role', 'button');

    $('.expand-glossary').on('click', function () {
        $('.panel-collapse').collapse('show');
        $(this).attr('aria-expanded', 'true');
        $('.collapse-glossary').attr('aria-expanded', 'true');
 

    });
    $('.collapse-glossary').on('click', function () {
        $('.panel-collapse').collapse('hide');
        $(this).attr('aria-expanded', 'false');
        $('.expand-glossary').attr('aria-expanded', 'false');
 
    });

});